    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
if( (this.TX1.getText() != null) && (!this.TX1.getText().equals(""))&&(this.TX2.getText() != null) && (!this.TX2.getText().equals(""))
     &&(this.TX3.getText() != null) && (!this.TX3.getText().equals(""))&&(this.TX4.getText() != null) && (!this.TX4.getText().equals(""))
     && (this.TX5.getText() != null) && (!this.TX5.getText().equals(""))){       
    
    
        Estados = this.TX1.getText().split(",");
        Lenguaje = this.TX2.getText().split(",");
        Matriz = new String[Estados.length][Lenguaje.length];
        DefaultTableModel model = new DefaultTableModel(Lenguaje, Estados.length);
        String k[] = {"."};
        String k1[][] = new String[Estados.length][1];
        for (int i = 0; i < Estados.length; i++) {
            k1[i][0] = Estados[i];
        }

        DefaultTableModel model1 = new DefaultTableModel(k1, k);

        Table.setModel(model);
        Table2.setModel(model1);
}else{
     JOptionPane.showMessageDialog(null, "Uno o algunos campos se encuentran Vacios\n Llene los campos");
}
         
         
         

        

         
       
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed


Finales  = TX4.getText().split(",");
Tiras    = TX5.getText();  
Estados  =TX1.getText().split(",");
Lenguaje =TX2.getText().split(",");
inicial=TX3.getText();
TableModel tableModel = Table.getModel();
int cols = tableModel.getColumnCount();
int fils = tableModel.getRowCount();
String [][] jk = new String [fils][cols];
//System.out.println("-----------------");
for(int i=0; i<fils; i++){
for(int j=0; j<cols; j++){
jk[i][j]=""+tableModel.getValueAt(i,j);
//System.out.print(""+jk[i][j]);
}
}
        
ValPrincipales val=new ValPrincipales ();

Object []results = val.RecoMatriz(Estados, Lenguaje, jk , Tiras, Finales, inicial);
tab.setText(""+results[0]);







    }//GEN-LAST:event_jButton2ActionPerformed

